import axios from 'axios';

export const store = {
    location: {
        latitude: null,
        longitude: null
    },
    weather: {

    },
    async getLocation() {
        return new Promise((resolve, reject) => {
            navigator.geolocation.getCurrentPosition(
                position => resolve(position.coords),
                error => reject(error)
            );
        });
    },
    async getWeather(latitude, longitude) {
        // FUTURE: add more customization options: C vs F
        const response = await axios.get(`https://api.open-meteo.com/v1/forecast?latitude=${latitude}&longitude=${longitude}&daily=sunrise,sunset,weather_code,temperature_2m_max,temperature_2m_min&hourly=temperature_2m,weather_code&current=temperature_2m,weather_code,is_day&timezone=auto&forecast_days=3&timeformat=unixtime&wind_speed_unit=mph&temperature_unit=fahrenheit&precipitation_unit=inch`)
        return response
    },
    async initialize() {
        this.location = await this.getLocation()
        this.weather = await this.getWeather(this.location.latitude, this.location.longitude)
    }
}
